import React, { useState } from 'react';
import './App.css';
import styles from './stylesExport'

function App() {

  const [divWidth, setDivWidth] = useState(styles.width);
  const [divHeight, setDivHeight] = useState(styles.height);

  const scale = 500 / 300;


  return (

    <div style={Object.assign(styles, { "height": divHeight.toString() + "px",
                                        "width": divWidth.toString() + "px"
                                      }
                              )
                }
      >

      <div id="number-inputs">

        <div id="height-input">

          <input
            type="number"
            name="height"
            value={divHeight * scale}
            min="1"
            max="500"
          />

        </div>

        <div id="width-input">

          <input
            type="number"
            name="width"
            value={divWidth * scale}
            min="1"
            max="500"
          />

        </div>

      </div>

      <div id="range-inputs">

        <div id="height-range-input">

          <input
            type="range"
            name="height"
            value={divHeight * scale}
            min="1"
            max="500"
          />

        </div>

        <div id="width-range-input">

          <input
            type="range"
            name="width"
            value={divWidth * scale}
            min="1"
            max="500"
          />

        </div>

      </div>

    </div>

  );

}

export default App;
