import React, { useState } from 'react';
import './App.css';
//import styles from './stylesExport'


function App() {

  const [divWidth, setDivWidth] = useState(300);
  const [divHeight, setDivHeight] = useState(300);

  const scale = 500 / 300;


  return (

    <div style={{ "width": divWidth,
                  "height": divHeight,
                  "color": "green",
                  "backgroundColor": "orange"
                }}
    >

      <div id="number-inputs">

        <div id="height-input">

          <input
            type="number"
            name="height"
            value={divHeight * scale}
            min="1"
            max="500"
          />

        </div>

        <div id="width-input">

          <input
            type="number"
            name="width"
            value={divWidth * scale}
            min="1"
            max="500"
          />

        </div>

      </div>

      <div id="range-inputs">

        <div id="height-range-input">

          <input
            type="range"
            name="height"
            value={divHeight * scale}
            min="1"
            max="500"
          />

        </div>

        <div id="width-range-input">

          <input
            type="range"
            name="width"
            value={divWidth * scale}
            onChange={e => setDivWidth(e.target.value)}
            min="1"
            max="500"
          />

        </div>

      </div>

    </div>

  );

}

export default App;
