import React, { useState } from 'react';
import Slider from './slider'
import './App.css';

function App() {

  const [divWidth, setDivWidth] = useState(500);
  const [divHeight, setDivHeight] = useState(500);

  const scale = 500 / 300;


  return (

    <div style={{ "width": divWidth / scale,
                  "height": divHeight / scale,
                  "backgroundColor": "green"
                }}
    >

      <div id="number-inputs">

        <div id="height-input">

          <input
            type="number"
            name="height"
            value={divHeight}
            onChange={e => setDivHeight(e.target.value)}
            min="1"
            max="500"
          />

        </div>

        <div id="width-input">

          <input
            type="number"
            name="width"
            value={divWidth}
            onChange={e => setDivWidth(e.target.value)}
            min="1"
            max="500"
          />

        </div>

      </div>

      <div id="range-inputs">

          <Slider value={divHeight} func={e => setDivHeight(e.target.value)} />
          <Slider value={divWidth} func={e => setDivWidth(e.target.value)} />

      </div>

    </div>

  );

}

export default App;
